import { GET_ALL_DOGS } from './actions-types'
import axios from 'axios'
const { MY_API_KEY } = process.env

export const getAllDogs = () => {
    return async (dispacht) => {
        const resApi = await axios.get(`https://api.thedogapi.com/v1/breeds?api_key=${MY_API_KEY}`)
        const dataApi = resApi.data

        const resDb = await axios.get('http://localhost:3001/dogs')
        const dataDb = resDb.data

        const allDogs = dataApi.concat(dataDb)

        return dispacht({
            type: GET_ALL_DOGS,
            payload: allDogs
        })
    }
}
