import React from 'react';
import { useDispatch, useSelector, connect } from 'react-redux';
import { useState, useEffect } from 'react';
import { addDog, getAllDogs, getAllTemperaments } from '../../redux/actions';
import style from '../Form/form.module.css';
import { Link } from 'react-router-dom';

const Form = () => {
    const dogs = useSelector((state) => state.allDogs);
    const temperaments = useSelector((state) => state.temperaments);

    const [form, setForm] = useState({

        image: "https://www.kindpng.com/picc/m/139-1396987_clip-art-cats-and-dogs-dibujos-de-perros.png",
        name: '',
        height: '',
        weight: '',
        life_span: '',
        temperaments: '',
    });



    const handleChange = (event) => {
      const property = event.target.name;
      const value = event.target.value;

      setForm({
        ...form,
        [property]: value,
      });
    };

    const handleSubmit = (event) => {
      event.preventDefault();
      dispatch(addDog(form));
      alert('Raza creada');
    };
    const handleSelectDificult = (e) => {
      setForm({
        ...form,
        dificult: e.target.value,
      });
    };

    const handleSelectDuration = (e) => {
      setForm({
        ...form,
        duration: e.target.value,
      });
    };

    const handleSelectUbication = (e) => {
      setForm({
        ...form,
        ubication: form.ubication.concat(e.target.value),
      });
    };

    const dispatch = useDispatch();
    useEffect(() => {
      dispatch(getAllDogs());
      dispatch(getAllTemperaments());
    }, []);

    return (
      <div className={style.div}>
        <form className={style.form} onSubmit={handleSubmit}>
          <Link to="/dogs" className={style.btn_home}>
            <button>Home</button>
          </Link>
          <div className={style.container}>
            <h2>Add Dogs Form</h2>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={form.name}
                onChange={handleChange}
              ></input>
            </label>

            <label>
            Height:
              <input
                type="text"
                name="height"
                value={form.height}
                onChange={handleChange}
              ></input>
            </label>

            <label>
            Weight:
            <input
                type="text"
                name="weight"
                value={form.weight}
                onChange={handleChange}
              ></input>
            </label>

            <label>
            Life Span:
              <input
                type="text"
                name="station"
                value={form.life_span}
                onChange={handleChange}
              />
            </label>

            <label>
            Ubication:
            <select
              className="select"
              name="temperaments"
              onChange={handleSelectUbication}
              value={form.id}
            >
              <option>temperaments</option>
              {temperaments?.map((e) => (
                <option key={e.name} value={e.name}>
                  {e.name}
                </option>
              ))}
            </select>
          </label>
            <div className={style.btn}>
              <button type="submit">Add Dog</button>
            </div>
          </div>
        </form>
      </div>
    );
  };

  const dispatchAct = (dispatch) => {
    return {
      addDog: (dog) => dispatch(addDog(dog)),
    };
  };

  //const filterCountries = countries.filter((e) => e.name === [activities].ubication)
  //? filterCountries.activities.push([activities])
  // : new Error ('el pais no existe')

  export default connect(null, dispatchAct)(Form);
