import { GET_ALL_DOGS, ORDER } from './actions-types'

const initialState = {
    allDogs: [],
    orderDogs: [],
}

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case GET_ALL_DOGS:
            return { 
            ...state,
            allDogs: action.payload
        }
         case ORDER: 
            const orderDogs = action.payload === 'A-Z' ? state.allDogs.sort(function (a,b) {
                if(a.name > b.name) {
                    return 1;
                  }
                  if (b.name > a.name) {
                    return -1;
                  }
                  return 0;
                })
              : state.allDogs.sort(function (a, b) {
                  if (a.name > b.name) {
                    return -1;
                  }
                  if (b.name > a.name) {
                    return 1;
                  }
                  return 0;
                });
            return {
                ...state,
                allDogs: orderDogs
            }   



        default:
        return { 
            ...state 
        };
    }
}

export default reducer;
